mapper.select:
select * from product_info as a LEFT JOIN product_sort as b on a.mk_product_type = b.ps_sort_id LEFT JOIN product_category as c on a.mk_product_category = c.pc_category_id
where ${searchField} like concat('%', #{keyword}, '%')
order by a.mk_idx desc limit #{pageFirst}, #{pageSize};

select count(*) from product_info		

where ${searchField} like concat('%', #{keyword}, '%')

order by mk_idx desc;
