mapper.select:
select * from product_info as a inner join product_sort as b on a.mk_product_type = b.ps_sort_id inner join product_category as c on a.mk_product_category = c.pc_category_id
where ${searchField} like concat('%', #{keyword}, '%')
order by a.mk_idx desc limit #{pageFirst}, #{pageSize};

select * 
from product_info as pr_info 
join product_sort as pr_sort 
on pr_info.mk_product_type = pr_sort.ps_sort_id 
where pr_info.mk_idx = #{mk_idx} 
order by pr_info.mk_idx desc;

mapper.update:
update product_info set mk_product_name = #{mk_product_name}, mk_product_id = #{mk_product_id}, mk_product_price = #{mk_product_price}, mk_product_category = #{mk_product_category}, mk_product_type = #{mk_product_type},
mk_original_thumb = #{mk_original_thumb}, mk_stored_thumb = #{mk_stored_thumb}, mk_thumb_size = #{mk_thumb_size},
mk_status = #{mk_status}, mk_content = #{mk_content}, mk_inventory = #{mk_inventory}, mk_writer_ip = #{mk_writer_ip} where mk_idx = ${mk_idx};

mapper.select:
select count(*) from product_info		

where ${searchField} like concat('%', #{keyword}, '%')

order by mk_idx desc;
