mapper.update:

update member 
set name = #{name}, nickname = #{nickname}, phone = #{phone}, pwd = #{pwd}, 
    email = #{email}, address1 = #{address1}, address2 = #{address2}, address3 = #{address3}, level = #{level} 
where id = #{id};

mapper.select:
select count(*) from member		

where ${searchField} like concat('%', #{keyword}, '%')

order by joindate desc;

select * from member

where ${searchField} like concat('%', #{keyword}, '%')

order by joindate desc limit #{pageFirst}, #{pageSize};
